import React, { useState } from 'react';
import { Line } from 'react-chartjs-2';

function PricesPage() {
    const [chartData, setChartData] = useState({});
    const [productId, setProductId] = useState('');
    const [granularity, setGranularity] = useState('');

    const fetchCandles = (e) => {
        e.preventDefault();
        const url = `http://localhost:3000/product-candles?productId=${productId}&granularity=${granularity}&start=2022-01-01&end=2022-01-02`; // Modify dates as needed
        fetch(url)
            .then(response => response.json())
            .then(data => {
                setChartData({
                    labels: data.map(d => new Date(d[0] * 1000).toLocaleTimeString()),
                    datasets: [
                        {
                            label: 'Price',
                            data: data.map(d => d[4]), // Closing prices
                            fill: false,
                            borderColor: 'rgb(75, 192, 192)',
                            tension: 0.1
                        }
                    ]
                });
            })
            .catch(error => console.error('Error fetching product candles:', error));
    };

    return (
        <div>
            <h1>Historical Prices</h1>
            <form onSubmit={fetchCandles}>
                <input type="text" value={productId} onChange={(e) => setProductId(e.target.value)} placeholder="Product ID" />
                <input type="text" value={granularity} onChange={(e) => setGranularity(e.target.value)} placeholder="Granularity (e.g., 3600)" />
                <button type="submit">Load Data</button>
            </form>
            <Line data={chartData} />
        </div>
    );
}

export default PricesPage;
