import React, { useState } from 'react';
import { Line } from 'react-chartjs-2';
import 'chart.js/auto';

function PricesPage() {
    const [chartData, setChartData] = useState({});
    const [productId, setProductId] = useState('');
    const [granularity, setGranularity] = useState('');
    const [startDate, setStartDate] = useState('');
    const [endDate, setEndDate] = useState('');

    const fetchCandles = (e) => {
        e.preventDefault();
        if (!productId || !granularity || !startDate || !endDate) {
            console.error('All fields are required');
            return;
        }

        const url = `http://localhost:4000/product-candles?productId=${productId}&granularity=${granularity}&start=${startDate}&end=${endDate}`;
        fetch(url)
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then(data => {
                if (!data.length) {
                    throw new Error('No data available');
                }
                setChartData({
                    labels: data.map(d => new Date(d[0] * 1000).toLocaleTimeString()),
                    datasets: [{
                        label: 'Price',
                        data: data.map(d => d[4]),
                        fill: false,
                        borderColor: 'rgb(75, 192, 192)',
                        tension: 0.1
                    }]
                });
            })
            .catch(error => {
                console.error('Error fetching product candles:', error);
            });
    };

    return (
        <div>
            <h1>Historical Prices</h1>
            <form onSubmit={fetchCandles}>
                <input type="text" value={productId} onChange={(e) => setProductId(e.target.value)} placeholder="Product ID" required />
                <input type="text" value={granularity} onChange={(e) => setGranularity(e.target.value)} placeholder="Granularity (e.g., 3600)" required />
                <input type="date" value={startDate} onChange={(e) => setStartDate(e.target.value)} required />
                <input type="date" value={endDate} onChange={(e) => setEndDate(e.target.value)} required />
                <button type="submit">Load Data</button>
            </form>
            {chartData && chartData.datasets && chartData.datasets.length > 0 ? (
                <Line data={chartData} />
            ) : (
                <p>No data to display</p>
            )}
        </div>
    );
}

export default PricesPage;
